Route::group([
    'prefix' => 'auth',
    'namespace' => 'Auth',
    'middleware' => 'api'
], function(){
    //cukup nama controller saja tidak perlu @ dan nama method, karena controller menggunakan __invoke (memanggil object seperti fungsi). dan controller hanya digunakan untuk satu fungsi saja. 
    Route::post('register', 'RegisterController');
    Route::post('regenerate-otp-code', 'RegenerateOtpCodeController');
    Route::post('verification', 'VerificationController');
    Route::post('update-password', 'UpdatePasswordController');
    Route::post('login', 'LoginController');
    Route::post('logout', 'LogoutController')->middleware('auth:api');
    Route::post('check-token', 'CheckTokenController')->middleware('auth:api');
});

Route::group([
    'prefix' => 'profile',
    'namespace' => 'User',
    'middleware' => ['email_verified', 'auth:api']
], function(){
    // Route::get('test', function () {
    //     return response()->json([
